package PratikJava;

public class GreatestNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
		int b = 10;
		int c = 15;
		
		if(a>b && a>c) {
			System.out.println("a is greatest");
		}
		else if(b>a && b>c) {
			System.out.println("b is greatest");
		}else {
			System.out.println("c is greatest");
		}

	}

}
